import React from 'react';import Menu from '@material-ui/core/Menu';import MenuItem from '@material-ui/core/MenuItem';const initialState = {  mouseX: null,  mouseY: null,};export default function ContextMenu({ children, items }) {  const [state, setState] = React.useState(initialState);  const handleClick = (event) => {    event.preventDefault();    setState({      mouseX: event.clientX - 2,      mouseY: event.clientY - 4,    });  };  const handleClose = () => {    setState(initialState);  };  if (!items || !items.length) return children;  return (    <div onContextMenu={handleClick} style={{ cursor: 'context-menu' }}>      {children}      <Menu        keepMounted        open={state.mouseY !== null}        onClose={handleClose}        anchorReference="anchorPosition"        anchorPosition={          state.mouseY !== null && state.mouseX !== null            ? { top: state.mouseY, left: state.mouseX }            : undefined        }      >        {items?.map(item => (          <MenuItem            key={item.label}            onClick={(event) => {              item.onClick(event);              handleClose();            }}>{item.label}</MenuItem>        ))}      </Menu>    </div>  );}